html,body{font-family:'Noto Sans JP',sans-serif;font-weight:normal;font-size:1em;color:#2f2f2f}h1,h2,h3,h4,h5,h6{padding:0;margin:0;color:#000;font-weight:bold;text-align:left}h1{font-size:1.7em}h2{margin:4px 0 12px 0}h3{margin:2px 0}section{margin:0 0 32px 0}article{margin:0 0 8px 0}a{color:#2A4073;text-decoration:none;border-bottom:1px dashed #2A4073;padding-bottom:0}table{border-spacing:0;width:100%}td,th{border-bottom:.1rem solid #e1e1e1;padding:1.2rem 1.5rem;text-align:left}td:first-child,th:first-child{padding-left:0}td:last-child,th:last-child{padding-right:0}.sns_share{text-align:center}header{margin:0;padding:0}section.articles article{padding:0;margin:0 0 36px 0}section.articles article p{padding:0;margin:0}dl.post_meta{padding:0;margin:2px 0 6px 0;color:#aaa;display:block}dl.post_meta dt,dl.post_meta dd{display:inline-block;padding:0;margin:0}dl.post_meta dt{margin-right:2px}dl.post_meta dd{margin-right:6px}code{padding:1px;margin:1px;background-color:#f8f8f8;display:inline-block;font-family:'Inconsolata',monospace}pre code{padding:8px;margin:0;display:block;border-left:3px solid #2A4073;font-family:'Inconsolata',monospace}pre{overflow-y:hidden}blockquote{padding:8px;margin:0;border-left:3px solid #d1d1d1}blockquote p{padding:0;margin:0}footer{margin:0;padding:10px;font-size:.8em;overflow:hidden;clear:both}footer div.copyright{margin:0;padding:0;float:right}footer nav,footer nav ul{margin:0;padding:0}footer nav{float:left}footer nav ul li{padding-right:6px;display:inline-block}@media screen and (max-width:1023px){main,header .header_eyecatch .header_pagetitle,footer{padding:8px}header .header_container{margin:0;padding:0;background-color:#2A4073}header .header_subtext{margin:0;padding:2px 10px;font-size:.7em;font-weight:100;display:block;color:#aaa;background-color:#2A4073}header .header_main{margin:0;padding:2px 10px 10px 10px;color:#fff;background-color:#2A4073;overflow:hidden;clear:both}header .header_main .brand{margin:0;padding:0;float:left;vertical-align:middle}header .header_main .brand a{display:inline-block}header .header_main a{color:#fff;text-decoration:none}header .header_main nav,header .header_main nav ul{margin:0;padding:0}header .header_main nav{float:right}header .header_main nav ul li{padding-left:6px;display:inline-block}}@media screen and (min-width:1024px){body{display:grid;grid-template-columns:320px 1fr}header{position:sticky;top:0;height:100vh}header .header_subtext{padding:0 2px;margin:0;color:#aaa;font-size:.7em}header .brand{padding:36px 0;text-align:center}header .brand a{text-decoration:none;border:0}header .header_main nav,header .header_main nav ul{margin:0;padding:0}header .header_main nav ul li{padding-left:12px}header .header_main nav ul li a{color:333;font-size:.9em;font-weight:bold;line-height:2.6em;text-decoration:none;border:0}main{margin:36px 0;padding:6px}main article h1{padding:42px 0}}